import os
import tkinter as tkt
from tkinter import filedialog
import imageio
import shutil
import cv2
from datetime import datetime
from PIL import Image, ImageTk, ImageSequence


class GifEngineer:
    def __init__(self):
        # For Images
        self.images = []
        self.imagesList = []
        self.imageEntns = ['.JPG', '.PNG', '.JPEG', '.TIFF']
        self.imageSrcDir = ''
        self.imageTrgtDir = ''
        self.tempDirName = '_temp'
        self.postFix = datetime.now()
        self.imageSrcDirTitle = 'Select Images Directory'

        # For GIF
        self.gifCanvas = ''
        self.gifSeq = ''
        self.gifImg = ''
        self.gifTrgtDirTitle = 'Save GIF @ Directory'
        self.gifBase64 = ''''''
        self.loadingLabel = 'Loading.'

    # create a frame to diaplay stuff
    def createFrame(self, window):
        frame = tkt.Frame(window, bg='#b5b5b5')  # frame creation
        frame.place(relwidth=0.8, relheight=0.8, relx=0.1, rely=0.1)
        return frame

    # select the directory path
    def loadImgSrcDir(self, window):
        frame = self.createFrame(window)
        self.imageSrcDir = filedialog.askdirectory(title=self.imageSrcDirTitle)

        if self.imageSrcDir:
            self.gifFromImages(self.imageSrcDir, frame)

    # select the target directory path
    def handleSaveCancel(self, action, gifPath, frame):
        if action == 'save':
            gifTrgtDir = filedialog.askdirectory(
                initialdir=self.imageSrcDir, title=self.gifTrgtDirTitle)

            if gifTrgtDir:
                shutil.copy(os.path.normpath(gifPath),
                            os.path.normpath(gifTrgtDir))
                frame.destroy()
                shutil.rmtree(self.imageTrgtDir)

        if action == 'cancel':
            os.remove(gifPath)
            frame.destroy()
            shutil.rmtree(self.imageTrgtDir)

    # form a Gif using images
    def gifFromImages(self, srcDirPath, frame):
        self.imageTrgtDir = os.path.join(self.imageSrcDir, self.tempDirName)
        if not os.path.isdir(self.imageTrgtDir):
            print('Temporary directory created.')
            os.mkdir(self.imageTrgtDir)
        gifFilename = 'gifFromImages' + \
            self.postFix.strftime('%m%d%Y_%H%M%S') + '.gif'

        print('Copying files into temporary directory..')
        # copy the images to a temp file for safety
        for filename in os.listdir(srcDirPath):
            source = srcDirPath + '/' + filename
            target = self.imageTrgtDir + '/' + filename
            if filename is not None and os.path.isfile(source):
                fName, fExt = os.path.splitext(filename)
                if fExt.upper() in self.imageEntns:
                    shutil.copyfile(source, target)
                    self.images.append(source)

        # create the gif and save to the destination
        try:
            print('Creating the GIF in temporary directory..')
            with imageio.get_writer(self.imageTrgtDir + '/' + gifFilename, mode='I') as writer:
                for filePath in self.images:
                    image = imageio.imread(filePath)
                    writer.append_data(image)
            print('Creation of Gif completed.')
        except:
            print('Error while creating the GIF.')

        self.saveCancelBtns(os.path.join(
            self.imageTrgtDir, gifFilename), frame)

    # Save and Cancel buttons
    def saveCancelBtns(self, gifPath, frame):
        self.renderGif(gifPath, frame)
        gifSaveBtnText = 'Save GIF @'
        gifCancelBtnText = 'Cancel/Delete Saved GIF'
        # Save Btn
        saveBtn = tkt.Button(frame, text=gifSaveBtnText, bg='#454545',
                             borderwidth=0, command=lambda: self.handleSaveCancel('save', gifPath, frame))  # lambda: frame.grid_forget())
        saveBtn.pack()
        saveBtn.place(bordermode='outside', x=0.1, y=0.1)

        # Cancel Btn
        cancelBtn = tkt.Button(frame, text=gifCancelBtnText, bg='#454545',
                               borderwidth=0, command=lambda: self.handleSaveCancel('cancel', gifPath, frame))
        cancelBtn.pack()
        cancelBtn.place(bordermode='outside', x=380, y=0.1)

    # Render the created Gif
    def renderGif(self, gifPath, parent):
        self.parent = parent
        self.gifCanvas = tkt.Canvas(parent, width=400, height=200)
        self.gifCanvas.pack()
        self.gifCanvas.place(bordermode='outside', x=85, y=50)
        self.gifSeq = [ImageTk.PhotoImage(img.resize((400, 250), Image.ANTIALIAS))
                       for img in ImageSequence.Iterator(Image.open(gifPath))]
        self.gifImg = self.gifCanvas.create_image(
            200, 75, image=self.gifSeq[0])
        self.animationSequence(1)

    def animationSequence(self, counter):
        self.gifCanvas.itemconfig(
            self.gifImg, image=self.gifSeq[counter])
        self.parent.after(20, lambda: self.animationSequence(
            (counter+1) % len(self.gifSeq)))

    # Clean up after window Close
    def windowClose(self, window):
        print('Closing the Application.')
        if self.imageTrgtDir:
            shutil.rmtree(self.imageTrgtDir)
